#pragma config(Motor,  port1,           FDL,           tmotorVex393_HBridge, openLoop, driveLeft)
#pragma config(Motor,  port3,           Shoulder,      tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port4,           Elbow,         tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port5,           Wrist,         tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port10,          FDR,           tmotorVex393_HBridge, openLoop, reversed, driveRight)
